/**
 * S017 Main Analyzer - Always use parameterized queries
 * Uses symbol-based analysis only (regex-based removed)
 * Command: node cli.js --rule=S017 --input=examples/rule-test-fixtures/rules/S017_use_parameterized_queries --engine=heuristic
 */

const S017SymbolBasedAnalyzer = require("./symbol-based-analyzer.js");

class S017Analyzer {
  constructor(options = {}) {
    if (process.env.SUNLINT_DEBUG) {
      console.log(`üîß [S017] Constructor called with options:`, !!options);
      console.log(
        `üîß [S017] Options type:`,
        typeof options,
        Object.keys(options || {})
      );
    }

    this.ruleId = "S017";
    this.ruleName = "Always use parameterized queries";
    this.description =
      "Always use parameterized queries instead of string concatenation to build SQL queries. This prevents SQL injection attacks by separating SQL logic from data";
    this.semanticEngine = options.semanticEngine || null;
    this.verbose = options.verbose || false;

    // Initialize symbol analyzer only
    try {
      this.symbolAnalyzer = new S017SymbolBasedAnalyzer(this.semanticEngine);
      if (process.env.SUNLINT_DEBUG) {
        console.log(`üîß [S017] Symbol analyzer created successfully`);
      }
    } catch (error) {
      console.error(`üîß [S017] Error creating symbol analyzer:`, error);
    }

    if (process.env.SUNLINT_DEBUG) {
      console.log(`üîß [S017] Constructor completed`);
    }
  }

  /**
   * Initialize with semantic engine
   */
  async initialize(semanticEngine = null) {
    if (semanticEngine) {
      this.semanticEngine = semanticEngine;
    }
    this.verbose = semanticEngine?.verbose || false;

    // Initialize symbol analyzer
    if (this.symbolAnalyzer) {
      await this.symbolAnalyzer.initialize?.(semanticEngine);
    }

    // Ensure verbose flag is propagated
    if (this.symbolAnalyzer) {
      this.symbolAnalyzer.verbose = this.verbose;
    }

    if (this.verbose) {
      console.log(
        `üîß [S017] Analyzer initialized - verbose: ${this.verbose}`
      );
    }
  }

  /**
   * Single file analysis method for testing
   */
  analyzeSingle(filePath, options = {}) {
    if (process.env.SUNLINT_DEBUG) {
      console.log(`üîç [S017] analyzeSingle() called for: ${filePath}`);
    }

    // Return result using same format as analyze method
    return this.analyze([filePath], "typescript", options);
  }

  async analyze(files, language, options = {}) {
    if (process.env.SUNLINT_DEBUG) {
      console.log(
        `üîß [S017] analyze() method called with ${files.length} files, language: ${language}`
      );
    }

    const violations = [];

    for (const filePath of files) {
      try {
        if (process.env.SUNLINT_DEBUG) {
          console.log(`üîß [S017] Processing file: ${filePath}`);
        }

        const fileViolations = await this.analyzeFile(filePath, options);
        violations.push(...fileViolations);

        if (process.env.SUNLINT_DEBUG) {
          console.log(
            `üîß [S017] File ${filePath}: Found ${fileViolations.length} violations`
          );
        }
      } catch (error) {
        console.warn(
          `‚ö† [S017] Analysis failed for ${filePath}:`,
          error.message
        );
      }
    }

    if (process.env.SUNLINT_DEBUG) {
      console.log(`üîß [S017] Total violations found: ${violations.length}`);
    }

    return violations;
  }

  async analyzeFile(filePath, options = {}) {
    if (process.env.SUNLINT_DEBUG) {
      console.log(`üîç [S017] analyzeFile() called for: ${filePath}`);
    }

    // Create a Set to track unique violations and prevent duplicates
    const violationMap = new Map();

    // Symbol-based analysis only
    if (this.semanticEngine?.project && this.semanticEngine?.initialized) {
      try {
        if (process.env.SUNLINT_DEBUG) {
          console.log(`üîß [S017] Running symbol-based analysis...`);
        }
        const sourceFile = this.semanticEngine.project.getSourceFile(filePath);
        if (sourceFile) {
          if (process.env.SUNLINT_DEBUG) {
            console.log(
              `üîß [S017] Source file found, analyzing with symbol-based...`
            );
          }

          // Read file content for symbol analyzer
          const fs = require("fs");
          const fileContent = fs.readFileSync(filePath, "utf8");

          const violations = await this.symbolAnalyzer.analyzeFile(
            filePath,
            fileContent,
            { ...options, verbose: options.verbose }
          );

          // Add violations to map to deduplicate
          violations.forEach((v) => {
            const key = `${v.line}:${v.column}:${v.message}`;
            if (!violationMap.has(key)) {
              v.analysisStrategy = "symbol-based";
              violationMap.set(key, v);
            }
          });

          if (process.env.SUNLINT_DEBUG) {
            console.log(
              `‚úÖ [S017] Symbol-based analysis: ${violations.length} violations`
            );
          }
          return Array.from(violationMap.values()); // Return deduplicated violations
        } else {
          if (process.env.SUNLINT_DEBUG) {
            console.log(`‚ö†Ô∏è [S017] Source file not found in project`);
          }
        }
      } catch (error) {
        console.warn(`‚ö†Ô∏è [S017] Symbol analysis failed: ${error.message}`);
      }
    } else {
      if (process.env.SUNLINT_DEBUG) {
        console.log(`üîÑ [S017] Symbol analysis conditions check:`);
        console.log(`  - semanticEngine: ${!!this.semanticEngine}`);
        console.log(
          `  - semanticEngine.project: ${!!this.semanticEngine?.project}`
        );
        console.log(
          `  - semanticEngine.initialized: ${this.semanticEngine?.initialized}`
        );
        console.log(`üîÑ [S017] Symbol analysis unavailable`);
      }
    }

    if (process.env.SUNLINT_DEBUG) {
      console.log(`üîß [S017] Analysis completed: ${violationMap.size} violations`);
    }
    return Array.from(violationMap.values());
  }

  /**
   * Methods for compatibility with different engine invocation patterns
   */
  async analyzeFileWithSymbols(filePath, options = {}) {
    return this.analyzeFile(filePath, options);
  }

  async analyzeWithSemantics(filePath, options = {}) {
    return this.analyzeFile(filePath, options);
  }

  /**
   * Get analyzer metadata
   */
  getMetadata() {
    return {
      rule: "S017",
      name: "Always use parameterized queries",
      category: "security",
      type: "hybrid",
      description:
        "Uses symbol-based and regex analysis to detect SQL injection vulnerabilities",
    };
  }
}

module.exports = S017Analyzer;
